import { Editor } from '@tinymce/tinymce-react';
import React, { Fragment, useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import slugify from 'react-slugify';

import { setAlert } from '../../actions/alertAction';
import { createProject, updateProject, uploadImage } from '../../actions/projectAction';

const BlogEditor = ({
  type,
  blog,
  id,
  url,
  progress,
  uploadImage,
  createProject,
  history,
  updateProject,
  setAlert,
}) => {
  const [data, setData] = useState({ slug: '', title: '', image: '', category: '', content: '' });
  const [flag, setFlag] = useState(false);

  useEffect(() => {
    setData({
      slug: blog ? blog.slug : '',
      title: blog ? blog.title : '',
      image: blog ? blog.image : '',
      category: blog ? blog.category : '',
      content: blog ? blog.content : '',
    });
  }, [blog]);

  useEffect(() => {
    if (url !== '' && flag) {
      setData({ ...data, image: url });
    }
  }, [url]);

  const titleChange = (e) => {
    setData({
      ...data,
      title: e.target.value,
      slug: slugify(e.target.value),
    });
  };

  const optionChange = (e) => {
    setData({
      ...data,
      category: e.target.value,
    });
  };

  const handleImageChange = (e) => {
    if (e.target.files[0]) {
      const image = e.target.files[0];
      setData({ ...data, image: image });
    }
  };
  const handleImageUpload = async (e) => {
    e.preventDefault();
    uploadImage(data.image);
    setFlag(true);
  };

  const handleChange = (e) => {
    setData({ ...data, content: e });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (type === 'create') {
      if (data.title !== '' && data.slug !== '' && data.image !== '' && data.category !== '' && data.content !== '') {
        createProject(data);
        history.push('/');
      } else {
        setAlert('Fill all the area.', 'error');
      }
    } else {
      if (data.title !== '' && data.slug !== '' && data.image !== '' && data.category !== '' && data.content !== '') {
        updateProject(
          {
            ...data,
            authorName: blog.authorName,
            authorImage: blog.authorImage,
            authorId: blog.authorId,
            ratings: blog.ratings,
          },
          id,
        );
        history.push('/');
      } else {
        setAlert('Fill all the area.', 'error');
      }
    }
  };

  return (
    <Fragment>
      <form onSubmit={handleSubmit} className='white'>
        <h5 className='grey-text text-darken-3'>{type === 'create' ? 'Create Blog' : 'Update Blog'}</h5>
        <div className='input-field'>
          <label htmlFor='title'>Title</label>
          <input className='center-align' value={data.title} type='text' onChange={titleChange} id='title' />
        </div>
        <div className='file-field input-field row'>
          <div className='col s6 m10'>
            <div className='btn'>
              <span>File</span>
              <input type='file' onChange={handleImageChange} />
            </div>
            <div className='file-path-wrapper'>
              <input className='file-path validate' placeholder='Upload your display image' type='text' />
            </div>
          </div>
          <a onClick={handleImageUpload} className='waves-effect waves-light btn'>
            Upload
          </a>
        </div>

        <div className='custom-align'>
          <img
            class='materialboxed'
            data-caption='Your Display image'
            width='250'
            src={
              data.image
                ? data.image
                : 'data:image/png;base64,'
            }
          />
        </div>
        <div class='progress col s6'>
          <div class='determinate' style={{ width: `${progress ? progress : 0}%` }}></div>
        </div>
        <div className='input-field'>
          <select onChange={optionChange} class='browser-default'>
            <option value={data.category} disabled selected>
              {data.category === '' ? 'Choose your blog category' : data.category}
            </option>
            <option value='Travelling'>Travelling</option>
            <option value='Sports'>Sports</option>
            <option value='Entertainment'>Entertainment</option>
            <option value='Technology'>Technology</option>
            <option value='Development'>Development</option>
          </select>
        </div>
        <div className='input-field'>
          <Editor
            value={data.content}
            init={{
              height: 500,
              menubar: true,
              plugins: [
                'advlist autolink lists link image charmap print preview',
                'searchreplace code fullscreen',
                'insertdatetime media table paste code help wordcount',
              ],
              file_picker_types: 'file image media',
              file_picker_callback: function (callback, value, meta) {
                var input = document.createElement('input');
                input.setAttribute('type', 'file');
                input.setAttribute('accept', 'image/*');
                input.onchange = function () {
                  var file = this.files[0];
                  var reader = new FileReader();
                  reader.onload = function (e) {
                    callback(e.target.result, {
                      alt: file.name,
                    });
                  };
                  reader.readAsDataURL(file);
                };
                input.click();
              },
            }}
            onEditorChange={handleChange}
          />
        </div>
        <div className='input-field'>
          <button className='btn pink lighten-1 z-depth-0'>{type === 'create' ? 'Create' : 'Update'}</button>
        </div>
      </form>
    </Fragment>
  );
};
const mapStateToProps = (state) => {
  return {
    url: state.blog.url,
    progress: state.blog.progress,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    createProject: (blog) => dispatch(createProject(blog)),
    uploadImage: (image) => dispatch(uploadImage(image)),
    updateProject: (blog, id) => dispatch(updateProject(blog, id)),
    setAlert: (msg, type) => dispatch(setAlert(msg, type)),
  };
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(BlogEditor));
